CREATE SEQUENCE IF NOT EXISTS  "ElectronicBulling".electronicdocument.EBSDOCUMENTTYPE START 1 INCREMENT 1 MINVALUE 1;

CREATE TABLE "ElectronicBulling".electronicdocument.EBTDOCUMENTTYPE
(
  ID NUMERIC(10) PRIMARY KEY DEFAULT nextval('"ElectronicBulling".electronicdocument.EBSDOCUMENTTYPE'),
  DOCUMENTCODE VARCHAR(2) UNIQUE NOT NULL ,
  DOCUMENTNAME VARCHAR(100) NOT NULL UNIQUE,
  DOCUMENTDETAILS VARCHAR(250) NOT NULL,
  STATUS_ID NUMERIC(10),
  STATUS_CODE VARCHAR(5),
  CONSTRAINT FK_DOCUMENTTYPE_STATUS FOREIGN KEY(STATUS_ID) REFERENCES "ElectronicBulling".electronicdocument.EBTSTATUS(ID) ON DELETE NO ACTION ON UPDATE CASCADE,
  CONSTRAINT FK_DOCUMENTTYPE_STATUSCODE FOREIGN KEY(STATUS_CODE) REFERENCES "ElectronicBulling".electronicdocument.EBTSTATUS(CODE) ON DELETE NO ACTION ON UPDATE CASCADE
);

